#AraAcctNoMgt@CheckFICdVAAccntNo
--AraAcctNoMgt@CheckFICdVAAccntNoSELECT COUNT(VA_ACCNT_NO) AS CNTFROM ARA_ACCT_MGTWHERE VA_ACCNT_NO = '@VA_ACCNT_NO'
#AraAcctNoMgt@Insert
--AraAcctNoMgt@InsertINSERT INTO ARA_ACCT_MGT (	VA_ACCNT_NO  , FI_CD  , USE_YN  , INS_USR  , INS_YMD  , INS_HM  , UPD_USR  , UPD_YMD  , UPD_HM)VALUES (	'@VA_ACCNT_NO'  , '@FI_CD'  , 'N'  , '@INS_USR'  , TO_CHAR(SYSDATE,'YYYYMMDD')  , TO_CHAR(SYSDATE,'HH24MISS')  , '@INS_USR'  , TO_CHAR(SYSDATE,'YYYYMMDD')  , TO_CHAR(SYSDATE,'HH24MISS'))
#AraAcctNoMgt@Search
--AraAcctNoMgt@SearchSELECT FI_CD	 , '' AS BANK_NM	 , COUNT(USE_YN) AS REMAIN_NOFROM ARA_ACCT_MGTWHERE USE_YN <> 'Y'GROUP BY FI_CDORDER BY FI_CD
#AraAcctNoMgt@SearchDetail
--AraAcctNoMgt@SearchDetailSELECT FI_CD	 , VA_ACCNT_NO	 , CASE	 	WHEN USE_YN = 'Y' THEN '사용'	 	WHEN USE_YN = 'N' THEN '미사용'	 	ELSE '' END AS USE_YN	 , TO_CHAR(TO_DATE(ISSUE_YMD,'YYYYMMDD'), 'YYYY-MM-DD') AS ISSUE_YMD	 , OFFICE_CD	 , OFFICE_NM	 , CASE	    WHEN LENGTH(BUNS_NO) >= 10 THEN SUBSTR(BUNS_NO, 0, 3) || '-' || SUBSTR(BUNS_NO, 4, 2) || '-' || SUBSTR(BUNS_NO, 6)		ELSE BUNS_NO END AS BUNS_NO 	 , STORE_ID	 , CURR_CD	 , AMOUNT	 , EMAIL_ADDR	 , INV_KEY	 , TEL_NO	 , TO_CHAR(TO_DATE(START_YMD,'YYYYMMDD'), 'YYYY-MM-DD') AS START_YMD	 , TO_CHAR(TO_DATE(END_YMD,'YYYYMMDD'), 'YYYY-MM-DD') AS END_YMD	 , GOODS_NM	 , ETC_NM	 , INS_USR	 , TO_CHAR(TO_DATE(INS_YMD,'YYYYMMDD'), 'YYYY-MM-DD') AS INS_YMD	 , TO_CHAR(TO_DATE(INS_HM,'hh24miss'), 'hh24:mi:ss') AS INS_HM	 , UPD_USR	 , TO_CHAR(TO_DATE(UPD_YMD,'YYYYMMDD'), 'YYYY-MM-DD') AS UPD_YMD	 , TO_CHAR(TO_DATE(UPD_HM,'hh24miss'), 'hh24:mi:ss') AS UPD_HMFROM ARA_ACCT_MGTWHERE FI_CD = '@FI_CD'  AND ROWNUM <= 500[USE_YN]  AND USE_YN = '@USE_YN'[/USE_YN][ISSUE_YMD_FROM]  AND ISSUE_YMD >= '@ISSUE_YMD_FROM'[/ISSUE_YMD_FROM][ISSUE_YMD_TO]  AND ISSUE_YMD <= '@ISSUE_YMD_TO'[/ISSUE_YMD_TO][OFFICE_NM]  AND OFFICE_NM LIKE '%@OFFICE_NM%'[/OFFICE_NM]ORDER BY VA_ACCNT_NO	   , USE_YN	   , ISSUE_YMD
#AraAcctNoMgt@Update
--AraAcctNoMgt@UpdateUPDATE ARA_ACCT_MGT SET[USE_YN]	USE_YN = '@USE_YN'[/USE_YN][ISSUE_YMD]  , ISSUE_YMD = '@ISSUE_YMD'[/ISSUE_YMD][OFFICE_NM]  , OFFICE_NM = '@OFFICE_NM'[/OFFICE_NM][BUNS_NO]  , BUNS_NO = '@BUNS_NO'[/BUNS_NO][STORE_ID]  , STORE_ID = '@STORE_ID'[/STORE_ID][CURR_CD]  , CURR_CD = '@CURR_CD'[/CURR_CD][AMOUNT]  , AMOUNT = '@AMOUNT'[/AMOUNT][INV_KEY]  , INV_KEY = '@INV_KEY'[/INV_KEY]  , UPD_USR = '@UPD_USR'  , TO_CHAR(SYSDATE,'YYYYMMDD')  , TO_CHAR(SYSDATE,'HH24MISS')WHERE FI_CD = '@FI_CD'  AND VA_ACCNT_NO = '@VA_ACCNT_NO'
