#ComSendMail@Insert
--ComSendMail@InsertINSERT INTO CRM_AS_MAIL (            MNGT_NO          , SEQ          , CUST_CD          , MSG_TYPE          , MSG_TYPE2          , MAIL_TYPE          , SEND_ID          , SEND_PWD          , SEND_HOST          , SEND_PORT          , SEND_SSL_YN          , SENDER          , RECEIVER          , CC          , SUBJECT          , CONTENT          , ATTACH_LIST          , INS_USR          , INS_YMD          , INS_HM   ) VALUES (            NVL('@S_MNGT_NO','@CUST_CD')          , (SELECT NVL(MAX(SEQ),0)+1 FROM CRM_AS_MAIL WHERE MNGT_NO = NVL('@S_MNGT_NO','@CUST_CD'))          , '@CUST_CD'          , '@S_MSG_TYPE'          , '@S_MSG_TYPE2'          , '@MAIL_TYPE'          , '@SEND_ID'          , '@SEND_PWD'          , '@SEND_HOST'          , '@SEND_PORT'          , '@SEND_SSL_YN'          , '@SENDER'          , '@RECEIVER'          , '@CC'          , '@SUBJECT'          , '@CONTENT'          , '@ATTACH_LIST'             , '@SSN_USR_ID'          , UFN_DATE_FORMAT('DATE')          , UFN_DATE_FORMAT('TIME')   )
#ComSendMail@Search
--ComSendMail@SearchSELECT 'N' AS CHK     , A.CUST_CD     , A.PIC_NM     , A.EMAIL     , A.SEQ FROM CRM_CUST_PIC AWHERE A.CUST_CD = '@CUST_CD'AND A.EMAIL IS NOT NULLORDER BY A.SEQ
#ComSendMail@SearchList
--ComSendMail@SearchListSELECT A.*     , UFN_FORMAT(A.INS_YMD,'DATE')||' '||UFN_FORMAT(A.INS_HM,'TIME') AS INS_DATE  FROM CRM_AS_MAIL A WHERE A.MNGT_NO = NVL('@MNGT_NO','@CUST_CD') [SEQ]   AND A.SEQ = '@SEQ' [/SEQ] ORDER BY MNGT_NO, SEQ DESC
#ComSendMail@SearchMailData
--ComSendMail@SearchMailDataSELECT (SELECT B.CD_NM FROM MDM_COM_CODE B WHERE B.GRP_CD = 'C03' AND B.COMN_CD = '@DEPT_CD') AS DEPT_NM     , A.SETUP_URL AS SETUP_URL     , A.DOMAIN AS DOMAIL     , '' AS VENDER_CD     , '' AS VENDER_NM     , A.CRN AS CRN     , A.CUSTOMS_CD AS CUSTOMS_CD     , A.CUST_NM AS CUST_NM     , A.CUST_ADDR AS CUST_ADDR   [MNGT_NO]     , B.CONTENT AS AS_CONTENT     , B.CMPT_RMK AS AS_CMPT_RMK   [/MNGT_NO]  FROM CRM_CUST_MST A     [MNGT_NO]       INNER JOIN CRM_AS_MST B               ON A.CUST_CD = B.CUST_CD              AND B.MNGT_NO = '@MNGT_NO'     [/MNGT_NO] WHERE 1 = 1[CUST_CD]   AND A.CUST_CD = '@CUST_CD'[/CUST_CD]
