#CrmManualMgt@Delete
--CrmManualMgt@DeleteDELETE  FROM CRM_MANUAL_MST WHERE MNGT_NO = '@MNGT_NO'
#CrmManualMgt@Save
--CrmManualMgt@SaveMERGE INTO CRM_MANUAL_MST DUSING (       SELECT '@MNGT_NO' AS MNGT_NO            , '@MANUAL_TYPE' AS MANUAL_TYPE            , '@REQ_SVC' AS REQ_SVC            , '@SYS_ID' AS SYS_ID            , '@CONTENT' AS CONTENT            , '@RMK' AS RMK            , '@SSN_USR_ID' AS INS_USR            , TO_CHAR(SYSDATE,'yyyyMMdd') AS INS_YMD            , TO_CHAR(SYSDATE,'HH24MISS') AS INS_HM            , '@SSN_USR_ID' AS UPD_USR            , TO_CHAR(SYSDATE,'yyyyMMdd') AS UPD_YMD            , TO_CHAR(SYSDATE,'HH24MISS') AS UPD_HM         FROM DUAL      ) S ON (D.MNGT_NO = S.MNGT_NO)WHEN MATCHED THEN     UPDATE         SET D.UPD_USR = S.UPD_USR          , D.UPD_YMD = S.UPD_YMD          , D.UPD_HM = S.UPD_HM       [MANUAL_TYPE], D.MANUAL_TYPE = S.MANUAL_TYPE[/MANUAL_TYPE]       [REQ_SVC], D.REQ_SVC = S.REQ_SVC[/REQ_SVC]       [SYS_ID], D.SYS_ID = S.SYS_ID[/SYS_ID]       [CONTENT], D.CONTENT = S.CONTENT[/CONTENT]       [RMK], D.RMK = S.RMK[/RMK]WHEN NOT MATCHED THEN     INSERT (         D.INS_USR, D.INS_YMD, D.INS_HM       , D.UPD_USR, D.UPD_YMD, D.UPD_HM       [MNGT_NO], D.MNGT_NO[/MNGT_NO]       [MANUAL_TYPE], D.MANUAL_TYPE[/MANUAL_TYPE]       [REQ_SVC], D.REQ_SVC[/REQ_SVC]       [SYS_ID], D.SYS_ID[/SYS_ID]       [CONTENT], D.CONTENT[/CONTENT]       [RMK], D.RMK[/RMK]     ) VALUES (         S.INS_USR, S.INS_YMD, S.INS_HM       , S.UPD_USR, S.UPD_YMD, S.UPD_HM       [MNGT_NO], S.MNGT_NO[/MNGT_NO]       [MANUAL_TYPE], S.MANUAL_TYPE[/MANUAL_TYPE]       [REQ_SVC], S.REQ_SVC[/REQ_SVC]       [SYS_ID], S.SYS_ID[/SYS_ID]       [CONTENT], S.CONTENT[/CONTENT]       [RMK], S.RMK[/RMK]     )
#CrmManualMgt@Search
--CrmManualMgt@SearchSELECT A.MNGT_NO     , A.MANUAL_TYPE     , A.REQ_SVC     , A.SYS_ID     , A.CONTENT     , A.RMK     , (SELECT B.LOC_NM FROM CRM_USR_MST B WHERE B.USR_ID = A.INS_USR) AS INS_USR     , TO_CHAR(TO_DATE(A.INS_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.INS_HM,'HH24MISS'), 'HH24:MI:SS') AS INS_YMD     , (SELECT B.LOC_NM FROM CRM_USR_MST B WHERE B.USR_ID = A.UPD_USR) AS UPD_USR     , TO_CHAR(TO_DATE(A.UPD_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.UPD_HM,'HH24MISS'), 'HH24:MI:SS') AS UPD_YMD  FROM CRM_MANUAL_MST A WHERE 1 = 1 [DATE_FM]
   [DATE_TYPE_REG]     AND A.INS_YMD >= '@DATE_FM'
   [/DATE_TYPE_REG] [/DATE_FM] [DATE_FM]
   [DATE_TYPE_REG]     AND A.INS_YMD <= '@DATE_TO'
   [/DATE_TYPE_REG] [/DATE_FM] [MNGT_NO]   AND A.MNGT_NO = '@MNGT_NO' [/MNGT_NO] ORDER BY A.MNGT_NO DESC
