#CrmSvcAprMgt@Insert_20201112
--CrmSvcAprMgt@InsertBEGIN	INSERT INTO MDM_EXT_USR_MST(	                        USR_ID 	                      , INS_USR	                      , INS_YMD 	                      , INS_HM 	                      , UPD_USR 	                      , UPD_YMD 	                      , UPD_HM 	                      , USE_YN                                  , USR_TYPE                            , EMAIL_YN                           , EMAIL_MSG   	             [LOC_NM] , LOC_NM        [/LOC_NM]	      [DEF_OFFICE_CD] , DEF_OFFICE_CD [/DEF_OFFICE_CD]                [CUST_CD] , CUST_CD       [/CUST_CD]                [CUST_NM] , CUST_NM       [/CUST_NM]                   [PSWD] , PSWD          [/PSWD]                  [HP_NO] , HP_NO         [/HP_NO]	              [EMAIL] , EMAIL         [/EMAIL]                    [CRN] , CRN           [/CRN]                 [APV_YN] , APV_YN        [/APV_YN]                     [AUTH_KEY]  , AUTH_KEY      [/AUTH_KEY]               )	                VALUES ( 	                        (SELECT TO_CHAR(SYSDATE,'YYYYMM') || LPAD(TO_NUMBER(NVL(MAX(SUBSTR(USR_ID,9)),0)) + 1,4,0)  	                            FROM MDM_EXT_USR_MST  WHERE USR_ID LIKE TO_CHAR(SYSDATE,'YYYYMM')|| '%' ) 	                     , '@WORK_USR_ID' 		                 , UFN_DATE_FORMAT('DATE')		                 , UFN_DATE_FORMAT('TIME') 		                 , '@WORK_USR_ID'		                 , UFN_DATE_FORMAT('DATE')		                 , UFN_DATE_FORMAT('TIME')		                 , '@USE_YN' 		                 , '@USR_TYPE' 		                 , '@EMAIL_YN' 		                 , '@EMAIL_MSG'		        [LOC_NM] , '@LOC_NM'        [/LOC_NM]         [DEF_OFFICE_CD] , '@DEF_OFFICE_CD' [/DEF_OFFICE_CD]               [CUST_CD] , '@CUST_CD'       [/CUST_CD]		       [CUST_NM] , '@CUST_NM'       [/CUST_NM]		          [PSWD] , UNF_MD5_ENCRYPTION@DL_PRIME_SYS('@PSWD') [/PSWD]                 [HP_NO] , '@HP_NO'         [/HP_NO]		         [EMAIL] , '@EMAIL'         [/EMAIL]                    [CRN] , '@CRN'           [/CRN]                [APV_YN] , '@APV_YN'        [/APV_YN]                  [AUTH_KEY]  , '@AUTH_KEY'      [/AUTH_KEY]		               );	INSERT INTO PRM_LOG_MST  ( MNGT_NO                           ,LOG_YMD                           ,WORK_USR_ID                           ,FLAG                           ,COL_NM                           ,OLD_VAL                           ,NEW_VAL                           )				    VALUES( '@EMAIL'				           ,UFN_DATE_FORMAT('DATE')				           ,'@WORK_USR_ID'				           ,'@FLAG'				           ,'@COL_NM'				           ,'@OLD_VAL'				           ,'@NEW_VAL'				           );			           END;
