#CrmSvcAprMgt@Search
--CrmSvcAprMgt@SearchSELECT 'N' AS CHK      --,'N' AS CHK_DUP      , A.OFFICE_NM      , A.LOC_NM      , A.EMAIL      , A.CRN      , A.ELVIS_ID      , A.ELVIS_ID AS DB_ELVIS_ID      , A.OFFICE_ADDR_CD      , A.OFFICE_ADDR      , A.OFFICE_ADDR2      , A.HP_NO      , A.TEL_NO      , A.APV_YN      , A.APV_YN AS DB_APV_YN      , A.EMAIL_MSG      , A.EMAIL_MSG AS DB_EMAIL_MSG      , UFN_FORMAT(A.REG_DATE, 'DATE') AS REG_DATE      , UFN_FORMAT(A.STAT_DATE, 'DATE') AS STAT_DATE      , A.CRM_CD      , A.CRM_CD AS DB_CRM_CD      , A.DOMAIN      , A.DOMAIN AS DB_DOMAIN      , A.SETUP_URL AS SETUP_URL      , NVL(A.ELVIS_FMS,'N') AS ELVIS_FMS      , NVL(A.ELVIS_WMS,'N') AS ELVIS_WMS      , NVL(A.ELVIS_TMS,'N') AS ELVIS_TMS      , NVL(A.ELVIS_EXPRESS,'N') AS ELVIS_EXPRESS      , NVL(A.ELVIS_PRIME,'N') AS ELVIS_PRIME      , NVL(A.ELVIS_BIG,'N') AS ELVIS_BIG      , NVL(A.ELVIS_BIG_UNI,'N') AS ELVIS_BIG_UNI      , NVL(A.ELVIS_BIG_SFI,'N') AS ELVIS_BIG_SFI      , NVL(A.ELVIS_BIG_TER,'N') AS ELVIS_BIG_TER      , NVL(A.ELVIS_BIG_TRK,'N') AS ELVIS_BIG_TRK      , NVL(A.ELVIS_BIG_LAT,'N') AS ELVIS_BIG_LAT      , NVL(A.ELVIS_FRIEND_DOCU,'N') AS ELVIS_FRIEND_DOCU      , NVL(A.ELVIS_FRIEND_EXIM,'N') AS ELVIS_FRIEND_EXIM      , NVL(A.ELVIS_FRIEND_DATA,'N') AS ELVIS_FRIEND_DATA      , NVL(A.ELVIS_FMS,'N') AS DB_ELVIS_FMS      , NVL(A.ELVIS_WMS,'N') AS DB_ELVIS_WMS      , NVL(A.ELVIS_TMS,'N') AS DB_ELVIS_TMS      , NVL(A.ELVIS_EXPRESS,'N') AS DB_ELVIS_EXPRESS      , NVL(A.ELVIS_PRIME,'N') AS DB_ELVIS_PRIME      , NVL(A.ELVIS_BIG,'N') AS DB_ELVIS_BIG      , NVL(A.ELVIS_BIG_UNI,'N') AS DB_ELVIS_BIG_UNI      , NVL(A.ELVIS_BIG_SFI,'N') AS DB_ELVIS_BIG_SFI      , NVL(A.ELVIS_BIG_TER,'N') AS DB_ELVIS_BIG_TER      , NVL(A.ELVIS_BIG_TRK,'N') AS DB_ELVIS_BIG_TRK      , NVL(A.ELVIS_BIG_LAT,'N') AS DB_ELVIS_BIG_LAT      , NVL(A.ELVIS_FRIEND_DOCU,'N') AS DB_ELVIS_FRIEND_DOCU      , NVL(A.ELVIS_FRIEND_EXIM,'N') AS DB_ELVIS_FRIEND_EXIM      , NVL(A.ELVIS_FRIEND_DATA,'N') AS DB_ELVIS_FRIEND_DATA   FROM PRM_ELVIS_REG A  WHERE 1=1      [EMAIL]        AND A.EMAIL LIKE '%@EMAIL%'      [/EMAIL]      [APV_YN]        AND A.APV_YN LIKE '@APV_YN'      [/APV_YN]      [OFFICE_NM]        AND A.OFFICE_NM LIKE '%@OFFICE_NM%'      [/OFFICE_NM]      [CRN]        AND A.CRN LIKE '%@CRN%'      [/CRN] ORDER BY (CASE WHEN A.APV_YN = 'N' THEN 1 ELSE (CASE WHEN A.APV_YN = 'Y' THEN 2 ELSE 3 END) END), A.INS_YMD DESC, A.INS_HM DESC 
