#CrmSvcAprMgt@SearchEmail
--CrmSvcAprMgt@SearchEmailSELECT A.CRN     , A.EMAIL     , A.APV_YN     , A.EMAIL_MSG     , (SELECT Z.CD_NM FROM MDM_COM_CODE Z WHERE Z.GRP_CD = 'R06' AND Z.COMN_CD = A.APV_YN) AS APV_YN_NM     , A.ELVIS_USR_ID     , A.ELVIS_PSWD     , A.OFFICE_NM AS CUST_NM     , SUBSTR((CASE WHEN A.ELVIS_FMS = 'Y' THEN ', FMS' END)                     ||(CASE WHEN A.ELVIS_WMS = 'Y' THEN ', WMS' END)                     ||(CASE WHEN A.ELVIS_TMS = 'Y' THEN ', TMS' END)                     ||(CASE WHEN A.ELVIS_EXPRESS = 'Y' THEN ', Express' END)                     ||(CASE WHEN A.ELVIS_PRIME = 'Y' THEN ', Prime' END)                     ||(CASE WHEN A.ELVIS_BIG = 'Y' THEN ', BIG(스케줄' END)                     ||(CASE WHEN A.ELVIS_BIG_UNI = 'Y' THEN ', 수입화물/수출이행' END)                     ||(CASE WHEN A.ELVIS_BIG_SFI = 'Y' THEN ', 해상운임' END)                     ||(CASE WHEN A.ELVIS_BIG_TER = 'Y' THEN ', 터미널' END)                     ||(CASE WHEN A.ELVIS_BIG_TRK = 'Y' THEN ', 화물추적' END)                     ||(CASE WHEN A.ELVIS_BIG_LAT = 'Y' THEN ', D/A 최적화' END)                     ||(CASE WHEN A.ELVIS_BIG = 'Y' THEN ')' END)                     ||(CASE WHEN A.ELVIS_FRIEND_DOCU = 'Y' THEN ', 견적/서식(문서판독)' END)                     ||(CASE WHEN A.ELVIS_FRIEND_EXIM = 'Y' THEN ', 수출입 화물' END)                     ||(CASE WHEN A.ELVIS_FRIEND_DATA = 'Y' THEN ', 물류분석 데이터' END),3) AS SVC_LIST     , TRIM(TO_CHAR(((CASE WHEN A.ELVIS_FMS = 'Y' THEN A.ELVIS_FMS_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_WMS = 'Y' THEN A.ELVIS_WMS_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_TMS = 'Y' THEN A.ELVIS_TMS_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_EXPRESS = 'Y' THEN A.ELVIS_EXPRESS_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_PRIME = 'Y' THEN A.ELVIS_PRIME_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG = 'Y' THEN A.ELVIS_BIG_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG_UNI = 'Y' THEN A.ELVIS_BIG_UNI_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG_SFI = 'Y' THEN A.ELVIS_BIG_SFI_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG_TER = 'Y' THEN A.ELVIS_BIG_TER_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG_TRK = 'Y' THEN A.ELVIS_BIG_TRK_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_BIG_LAT = 'Y' THEN A.ELVIS_BIG_LAT_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_FRIEND_DOCU = 'Y' THEN A.ELVIS_FRIEND_DOCU_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_FRIEND_EXIM = 'Y' THEN A.ELVIS_FRIEND_EXIM_PRICE ELSE 0 END)                    +(CASE WHEN A.ELVIS_FRIEND_DATA = 'Y' THEN A.ELVIS_FRIEND_DATA_PRICE ELSE 0 END)),'999,999,999,999,999')) AS SVC_TOT_AMT     , (CASE WHEN A.REG_DATE IS NULL THEN '' ELSE SUBSTR(A.REG_DATE,1,4)||'-'||SUBSTR(A.REG_DATE,5,2)||'-'||SUBSTR(A.REG_DATE,7,2) END) AS REG_DATE     , A.DOMAIN AS DOMAIN     , A.SETUP_URL AS SETUP_URL     , '' AS RMK     , (SELECT USR_ID FROM FRND_USR_MST WHERE USR_ID = A.EMAIL AND CRN = A.CRN) AS FRND_USR_ID  FROM PRM_ELVIS_REG A WHERE A.CRN IN ('@CRN')
