     , ELVIS_FRIEND_DATA_PRICE = CASE WHEN '@ELVIS_FRIEND_DATA' = 'Y' THEN 10000 ELSE 0 END    [APV_YN]     , APV_YN = '@APV_YN'     , ELVIS_PSWD = (CASE WHEN ELVIS_PSWD IS NULL THEN '@NEW_ELVIS_PSWD' ELSE ELVIS_PSWD END)     , UPD_USR = '@USR_ID'     , UPD_YMD = TO_CHAR(SYSDATE, 'YYYYMMDD')     , UPD_HM = TO_CHAR(SYSDATE, 'HH24MISS')   [/APV_YN] WHERE CRN = '@CRN'
