--EvbMembers@Search--SELECT M.USER_AUTH_ID     , M.BUSN_NM     , M.BUSN_NO  FROM EVB_MEMB_SET M WHERE 1=1     [SEARCH_KEY]       AND ( 1=2       OR M.BUSN_NO LIKE REPLACE('%@SEARCH_KEY%', '-','')       OR M.BUSN_NM LIKE '%@SEARCH_KEY%'       OR M.USER_AUTH_ID LIKE '@SEARCH_KEY%'      )     [/SEARCH_KEY]     [USE_YN]       AND M.MEMB_STATE IN ('S','A','E',(CASE WHEN '@USE_YN' = 'Y' THEN 'C' ELSE '' END))                [/USE_YN]ORDER BY M.BUSN_NM
