#HelpBwsSqlExecuter_v2@SearchDomain
SELECT SYS.CUST_NM     , SYS.CUST_LOC_NM     , SYS.DOMAIN     , SYS.USR_ID     , SYS.USR_PWD     , SYS.DB_IP     , SYS.DB_PORT     , SYS.DB_SID     , SYS.CTRY_CD  FROM SYS_CUST_MST@ELVIS_PLUS SYS  JOIN CRM_CUST_MST CRM    ON SYS.TNS_NM = CRM.DOMAIN WHERE CRM.CUST_CD = '@CUST_CD' ORDER BY SYS.CUST_NM
#HelpBwsSqlExecuter_v2@SearchIsu
SELECT ISM.ISU_MNGT_NO     , NVL(ISM.RPM_MNGT_NO,RPM.RPM_MNGT_NO) AS RPNO     , RPM.LICENSE_NO     , ISM.GI_TYPE     , ISD.QTY     , ISD.WGT     , ISD.CBM     , ISM.SEND_ACK_REMARK     , ISM.SEND_NO     , (CASE WHEN (ISM.RPM_MNGT_NO IS NULL AND ISM.ISU_MNGT_NO IS NOT NULL) THEN '미승인반출'              WHEN ISM.ISU_MNGT_NO IS NULL THEN '승인'             ELSE '반출' END) AS GBUN  FROM BWS_RPM_MST RPM      FULL OUTER  JOIN BWS_ISU_MST ISM    ON RPM.RPM_MNGT_NO = ISM.RPM_MNGT_NO   AND ISM.OFFICE_CD = '@OFFICE_CD'   AND ISM.BRANCH_CD = '@BRANCH_CD'  FULL OUTER  JOIN BWS_ISU_DTL ISD    ON ISM.ISU_MNGT_NO = ISD.ISU_MNGT_NO WHERE RPM.OFFICE_CD = '@OFFICE_CD'   AND RPM.BRANCH_CD = '@BRANCH_CD'   
#HelpBwsSqlExecuter_v2@SearchOffice
SELECT OFFICE_CD, BRANCH_CD, BRANCH_NM  FROM MDM_BRANCH_MST WHERE CUSTOMS_WH_CODE IS NOT NULL
#HelpBwsSqlExecuter_v2@SearchRcp
SELECT RCD.RCP_MNGT_NO     , EGM.EGR_MNGT_NO     , RCM.LICENSE_NO     , RCD.QTY     , RCD.WGT     , RCD.CBM     , RCM.SEND_ACK_REMARK     , RCM.SEND_NO     , (CASE WHEN RCM.RCP_MNGT_NO IS NULL THEN '미반입' ELSE '반입' END) AS GBUN     , EGM.HBL_NO     , EGM.MRN||EGM.MSN||EGM.HSN AS MRN  FROM BWS_EGR_MST EGM  JOIN BWS_EGR_DTL EGD    ON EGM.EGR_MNGT_NO = EGD.EGR_MNGT_NO  LEFT  JOIN BWS_RCP_DTL RCD    ON EGM.EGR_MNGT_NO = RCD.EGR_MNGT_NO  LEFT  JOIN BWS_RCP_MST RCM    ON RCD.RCP_MNGT_NO = RCM.RCP_MNGT_NO WHERE EGM.OFFICE_CD = '@S_OFFICE_CD'   AND EGM.BRANCH_CD = '@S_BRANCH_CD'   AND (EGM.HBL_NO = '@HBL_NO' OR (EGM.MRN||EGM.MSN||EGM.HSN) = '@MRN')
#HelpBwsSqlExecuter_v2@SearchSql
SELECT SQL_ID     , TITLE     , SQL_STR      , APPLY_SOURCE      , (SELECT CD_NM FROM MDM_COM_CODE WHERE GRP_CD = 'H01' AND  COMN_CD =  APPLY_SOURCE) AS APPLY_SOURCE_NM     FROM SYS_ALTER_TABLE WHERE APPLY_SOURCE = '09'   AND USE_YN = 'Y' ORDER BY SQL_ID
