T         , USD_CONV_RT		, RMK				  , INS_USR				, INS_YMD			, INS_HM					  , UPD_USR  			, UPD_YMD			, UPD_HM			  )     VALUES (			'@OFFICE_CD'		, '@BASE_CURR'		, '@CURR_CD'					      , '@EXRT_YMD'			, @CURR_UNIT		  , @CASH_BUYRT			, @CASH_SELLRT		  , @WIRE_SEND_EXRT		, @WIRE_RECV_EXRT		  , @EXCH_BASERT		, @USD_CONV_RT 		, '@RMK'						  , '@USR_ID'			, UFN_DATE_FORMAT('DATE')	 	, UFN_DATE_FORMAT('TIME')		  , '@USR_ID'			, UFN_DATE_FORMAT('DATE')	    , UFN_DATE_FORMAT('TIME')		  ) 		  
