-- MdmExrateMgt@UpdateMonthlyUPDATE MDM_MM_EX_RATE    SET CURR_UNIT		= '@CURR_UNIT'     , CASH_BUYRT		= 'CASH_BUYRT     , CASH_SELLRT		= NVL(@CASH_SELLRT,0)     , WIRE_SEND_EXRT	= NVL(@WIRE_SEND_EXRT,0)     , WIRE_RECV_EXRT	= NVL(@WIRE_RECV_EXRT,0)     , EXCH_BASERT		= NVL(@EXCH_BASERT,0)     , USD_CONV_RT		= NVL(@USD_CONV_RT,0)     , UPD_USR			= '@USR_ID'     , UPD_YMD			= UFN_DATE_FORMAT('DATE')     , UPD_HM			= UFN_DATE_FORMAT('TIME')WHERE  OFFICE_CD 		= '@OFFICE_CD'  AND  BASE_CURR 		= '@BASE_CURR'  AND  CURR_CD 			= '@CURR_CD'  AND  EXRT_MM 		    = '@EXRT_MM'
#MdmExrateMgt@WeekDay
--MdmExrateMgt@WeekDaySELECT TO_CHAR( TO_DATE('@EX_DATE', 'YYYYMMDD'),'D')AS EX_DATE FROM DUAL
