#StlPaymentList@Search
--StlPaymentList@SearchSELECT A.MNGT_NO     , A.INS_DATE     , A.WORK_USR     , A.WORK_USR_NM     , A.CUST_CD     , A.CUST_NM     , A.REQ_YMD     , A.REQ_HM     , A.REQ_DATE     , UFN_GET_NAME_ADDR(A.CONTENT,'NAME') AS METTING_RMK     , A.FILE_YN     , A.CMPT_DATE     , A.ORGN_MNGT_NO     , A.UPPER_MNGT_NO     , (SELECT TO_CD FROM MDM_MAPPING_MST WHERE OFFICE_CD = '@OFFICE_CD' AND TABLE_ID = 'CRM_AS_PAY' AND FM_TYPE = 'YJACC_DEPT' AND FM_CD = A.DEPT_CD) AS DEPT_CD     , (SELECT TO_NM FROM MDM_MAPPING_MST WHERE OFFICE_CD = '@OFFICE_CD' AND TABLE_ID = 'CRM_AS_PAY' AND FM_TYPE = 'YJACC_DEPT' AND FM_CD = A.DEPT_CD) AS DEPT_NM     , (SELECT TO_CD FROM MDM_MAPPING_MST WHERE OFFICE_CD = '@OFFICE_CD' AND TABLE_ID = 'CRM_AS_PAY' AND FM_TYPE = 'YJACC_PROJECT' AND FM_CD = A.PROJECT_CD) AS PROJECT_CD     , (SELECT TO_NM FROM MDM_MAPPING_MST WHERE OFFICE_CD = '@OFFICE_CD' AND TABLE_ID = 'CRM_AS_PAY' AND FM_TYPE = 'YJACC_PROJECT' AND FM_CD = A.PROJECT_CD) AS PROJECT_NM     , B.SEQ     , B.FARE_LOC_AMT     , B.FARE_VAT_AMT     , (B.FARE_LOC_AMT+B.FARE_VAT_AMT) AS FARE_SUM_AMT     , B.RMK AS PAY_RMK     , B.CARD_CRN     , B.CARD_CUST_NM     , B.CARD_CD     , B.CARD_NM     , B.CARD_ACCT_CD     , (SELECT Z.CD_NM FROM MDM_COM_CODE Z WHERE Z.GRP_CD = 'T18' AND Z.COMN_CD = B.CARD_ACCT_CD AND ROWNUM = 1) AS CARD_ACCT_NM     , B.CARD_TYPE     , (SELECT Z.CD_NM FROM MDM_COM_CODE Z WHERE Z.GRP_CD = 'T19' AND Z.COMN_CD = B.CARD_TYPE AND ROWNUM = 1) AS CARD_TYPE_NM  FROM VT_CRM_AS_MST A       INNER JOIN CRM_AS_PAY B               ON A.MNGT_NO = B.MNGT_NO WHERE A.EX_IM_TYPE = 'E'   AND (A.SEQ = '0' OR A.SEQ IS NULL)    [REQ_YMD]            [FM_YMD]               AND A.REQ_YMD >= '@FM_YMD'            [/FM_YMD]            [TO_YMD]               AND A.REQ_YMD <= '@TO_YMD'            [/TO_YMD]    [/REQ_YMD]         [CMPT_YMD]             [FM_YMD]               AND A.CMPT_YMD >= '@FM_YMD'            [/FM_YMD]            [TO_YMD]               AND A.CMPT_YMD <= '@TO_YMD'            [/TO_YMD]    [/CMPT_YMD]        [DEPT_CD]      [DEPT_TYPE_I]        AND A.DEPT_CD = '@DEPT_CD'      [/DEPT_TYPE_I]      [DEPT_TYPE_W]        AND A.DEPT_CD = '@DEPT_CD'      [/DEPT_TYPE_W]      [DEPT_TYPE_M]        AND A.DEPT_CD IN ('@DEPT_CD')      [/DEPT_TYPE_M]    [/DEPT_CD]    [CUST_CD]       AND A.CUST_CD = '@CUST_CD'    [/CUST_CD]    [WORK_TYPE_W]      [WORK_USR]         --20220726 김성은 COST가 높아서 VT_CRM_AS_MST과 함께 변경         --AND (A.WORK_USR = (SELECT MAX(Z.LOC_NM) FROM CRM_USR_MST Z WHERE Z.USR_ID = '@WORK_USR')         --     OR A.WORK_USR = '@WORK_USR'         --    )         AND (A.WORK_USR = (SELECT MAX(Z.USR_ID) FROM CRM_USR_MST Z WHERE Z.LOC_NM = '@WORK_USR' AND USE_YN = 'Y')              OR A.WORK_USR = '@WORK_USR'             )      [/WORK_USR]    [/WORK_TYPE_W]    [WORK_TYPE_I]      [WORK_USR]         AND A.INS_USR = '@WORK_USR'       [/WORK_USR]    [/WORK_TYPE_I]    [SEARCH_KEY]       AND ( 1 != 1       OR A.CUST_CD LIKE '%@SEARCH_KEY%'       OR A.TEL_NO LIKE '%@SEARCH_KEY%'       OR A.REQ_USR LIKE '%@SEARCH_KEY%'       OR A.MNGT_NO LIKE '%@SEARCH_KEY%'       OR A.CONTENT LIKE '%@SEARCH_KEY%'       OR A.MNGT_NO IN (SELECT MNGT_NO                          FROM CRM_AS_MST_WORK                         WHERE USR_ID IN (SELECT VALUE1                          FROM TABLE(  UFN_GET_INFO('USER_LIST','%@SEARCH_KEY%')))  )       OR EXISTS (SELECT Z.CUST_CD                             FROM CRM_CUST_MST Z                            WHERE Z.CUST_CD = A.CUST_CD                             AND (    Z.CUSTOMS_CD LIKE '%@SEARCH_KEY%'                                   OR Z.CUST_NM LIKE '%@SEARCH_KEY%'                                   OR Z.CUST_LOC_NM LIKE '%@SEARCH_KEY%'                                   OR Z.DOMAIN LIKE '%@SEARCH_KEY%'                                      )                      )      )    [/SEARCH_KEY]    [PROJECT_CD]      AND PROJECT_CD = '@PROJECT_CD'    [/PROJECT_CD]    [CARD_ACCT_CD]      AND CARD_ACCT_CD = '@CARD_ACCT_CD'    [/CARD_ACCT_CD]        [CARD_TYPE]      AND CARD_TYPE = '@CARD_TYPE'    [/CARD_TYPE]    ORDER BY B.CARD_TYPE, A.REQ_YMD DESC, A.REQ_HM DESC, A.MNGT_NO DESC
