#SvrModuleMgt@Search
--SvrModuleMgt@SearchSELECT A.MNGT_NO     , A.SERVER_IP     , A.SERVER_IP_EXT     , A.SERVER_TYPE     , A.SERVER_NAME     , A.LOC_TYPE     , A.CTRT_YMD     , A.CTRT_AMT     , A.MATN_START_YMD     , A.MATN_END_YMD     , A.MATN_AMT     , A.USE_YN     , A.SAVE_CNT     , A.RMK     , A.VAC_TYPE     , A.RANSOM_TYPE     , A.CUST_CNT     , A.SERVER_RMK     , A.OS_TYPE     , A.SERVER_IP_EXT, (SELECT COUNT(*) FROM SYS_CUST_MST@ELVIS B WHERE B.WEB_IP_EXT = A.SERVER_IP_EXT) AS WEBIP_CNT     , A.SERVER_IP_EXT, (SELECT COUNT(*) FROM SYS_CUST_MST@ELVIS B WHERE B.DB_IP_EXT = A.SERVER_IP_EXT) AS DBIP_CNT     , (SELECT Z.LOC_NM FROM CRM_USR_MST Z WHERE Z.USR_ID = A.INS_USR) AS INS_USR     , TO_CHAR(TO_DATE(A.INS_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.INS_HM,'HH24MISS'), 'HH24:MI:SS') AS INS_YMD     , (SELECT Z.LOC_NM FROM CRM_USR_MST Z WHERE Z.USR_ID = A.UPD_USR) AS UPD_USR     , TO_CHAR(TO_DATE(A.UPD_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.UPD_HM,'HH24MISS'), 'HH24:MI:SS') AS UPD_YMD  FROM CRM_SERVER_MST A WHERE 1 = 1 [DATE_FROM]   AND A.INS_YMD >= '@DATE_FROM' [/DATE_FROM] [DATE_TO]   AND A.INS_YMD <= '@DATE_TO' [/DATE_TO] [SEARCH_KEY_TYPE_A]   AND (A.SERVER_IP = '@SEARCH_KEY'        OR A.SERVER_IP_EXT = '@SEARCH_KEY'       ) [/SEARCH_KEY_TYPE_A] [SEARCH_KEY_TYPE_B]   AND A.SERVER_IP = '@SEARCH_KEY' [/SEARCH_KEY_TYPE_B] [SEARCH_KEY_TYPE_C]   AND A.SERVER_IP_EXT = '@SEARCH_KEY' [/SEARCH_KEY_TYPE_C] [USE_YN]   AND A.USE_YN = '@USE_YN' [/USE_YN]
#SvrModuleMgt@SearchModule
--SvrModuleMgt@SearchModuleSELECT A.MNGT_NO     , A.SERVER_IP     , A.SERVER_IP_EXT     , B.MODULE_TYPE     , B.LOOP_TYPE     , B.LOOP_VALUE     , B.PROC_WAIT_SEC     , B.PROC_LOG_SEC     , B.SMS_LIST     , B.EMAIL_LIST     , B.KAKAOPC_LIST     , B.USE_YN     , (SELECT Z.LOC_NM FROM CRM_USR_MST Z WHERE Z.USR_ID = B.INS_USR) AS INS_USR     , TO_CHAR(TO_DATE(B.INS_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(B.INS_HM,'HH24MISS'), 'HH24:MI:SS') AS INS_YMD     , (SELECT Z.LOC_NM FROM CRM_USR_MST Z WHERE Z.USR_ID = B.UPD_USR) AS UPD_USR     , TO_CHAR(TO_DATE(B.UPD_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(B.UPD_HM,'HH24MISS'), 'HH24:MI:SS') AS UPD_YMD  FROM CRM_SERVER_MST A           INNER JOIN CRM_SERVER_MODULE B                       ON A.MNGT_NO = B.MNGT_NO                       AND B.USE_YN = 'Y'                    [MODULE_TYPE]                      AND B.MODULE_TYPE = '@MODULE_TYPE'                    [/MODULE_TYPE] WHERE 1 = 1 [MNGT_NO]   AND A.MNGT_NO = '@MNGT_NO' [/MNGT_NO] [SERVER_IP]   AND A.SERVER_IP = '@SERVER_IP' [/SERVER_IP]
#SvrModuleMgt@SearchModuleAutoCheck
WITH W_PROC_CHKAS(        SELECT '모듈 작동여부를 확인해 주세요.'||' '               ||CHR(13)||CHR(10)||'IP:'||A.SERVER_IP||' '               ||CHR(13)||CHR(10)||'모듈명:'||NVL((SELECT Z.CD_NM FROM MDM_COM_CODE Z WHERE GRP_CD = 'SVR01' AND COMN_CD = B.MODULE_TYPE),B.MODULE_TYPE)||' ' AS CHK_MSG             , A.MNGT_NO             , A.SERVER_IP             , A.SERVER_IP_EXT             , A.SERVER_TYPE             , B.MODULE_TYPE             , B.SMS_LIST             , B.EMAIL_LIST             , B.KAKAOPC_LIST             , TO_CHAR(TO_DATE(SUBSTR(C.LOG_TIMESTAMP,1,8),'YYYYMMDD'),'YYYY-MM-DD')||' '||TO_CHAR(TO_DATE(SUBSTR(C.LOG_TIMESTAMP,9,6),'HH24MISS'), 'HH24:MI:SS') AS LOG_TIMESTAMP          FROM CRM_SERVER_MST A               INNER JOIN CRM_SERVER_MODULE B                       ON A.MNGT_NO = B.MNGT_NO AND B.USE_YN = 'Y' --AND MODULE_TYPE = 'CK'               INNER JOIN CRM_SERVER_MODULE_LOG C                       ON B.MNGT_NO = C.MNGT_NO AND B.MODULE_TYPE = C.MODULE_TYPE                      AND C.USE_YN = 'Y'                      AND ((SYSDATE-TO_DATE(C.INS_YMD||C.INS_HM,'YYYYMMDD HH24:MI:SS')) *24 * 60* 60) > B.PROC_LOG_SEC), W_SETTING_CHKAS(         SELECT '모듈을 셋팅해 주세요.'||' '               ||CHR(13)||CHR(10)||'IP:'||A.SERVER_IP||' '               ||CHR(13)||CHR(10)||'모듈명:'||NVL((SELECT Z.CD_NM FROM MDM_COM_CODE Z WHERE GRP_CD = 'SVR01' AND COMN_CD = B.MODULE_TYPE),B.MODULE_TYPE)||' ' AS CHK_MSG             , A.MNGT_NO             , A.SERVER_IP             , A.SERVER_IP_EXT             , A.SERVER_TYPE             , B.MODULE_TYPE             , B.SMS_LIST             , B.EMAIL_LIST             , B.KAKAOPC_LIST             , '' AS LOG_TIMESTAMP          FROM CRM_SERVER_MST A               INNER JOIN CRM_SERVER_MODULE B                       ON A.MNGT_NO = B.MNGT_NO AND B.USE_YN = 'Y' --AND MODULE_TYPE = 'CK'               LEFT OUTER JOIN CRM_SERVER_MODULE_LOG C                       ON B.MNGT_NO = C.MNGT_NO AND B.MODULE_TYPE = C.MODULE_TYPE                      AND C.USE_YN = 'Y'        WHERE C.MNGT_NO IS NULL)SELECT A.*  FROM W_PROC_CHK AUNION ALL SELECT B.*  FROM W_SETTING_CHK BORDER BY 3
#SvrModuleMgt@SearchModuleLogCheck
--SvrModuleMgt@SearchModuleLogCheckSELECT A.MNGT_NO          , A.SERVER_IP          , B.MODULE_TYPE          , C.* FROM CRM_SERVER_MST A         INNER JOIN CRM_SERVER_MODULE B         ON A.MNGT_NO = B.MNGT_NO AND MODULE_TYPE = 'CK'         LEFT OUTER JOIN CRM_SERVER_MODULE_LOG C         ON B.MNGT_NO = C.MNGT_NO AND B.MODULE_TYPE = C.MODULE_TYPE          AND C.INS_YMD = TRIM(TO_CHAR(SYSDATE,'YYYYMMDD'))         AND C.INS_HM >= TRIM(TO_CHAR((SYSTIMESTAMP - PROC_LOG_SEC),'HHMMDD'))         AND C.USE_YN = 'Y'--         LEFT OUTER JOIN CRM_SERVER_INFO_LOG C--         ON A.SERVER_IP = C.SERVER_IP AND DATA_DIV = 'SC' --         AND CHECK_YMD = TRIM(TO_CHAR(SYSDATE,'YYYYMMDD'))--         AND CHECK_HM >= TRIM(TO_CHAR((SYSTIMESTAMP - INTERVAL '5' MINUTE),'HHMMDD'))--         AND LAST_DATA_YN = 'Y'WHERE C.MNGT_NO IS NULLORDER BY C.MNGT_NO
