--SvrModuleMgt@SearchRPAInfoSELECT PKG_CRYPTO.DECRYPT(A.DB_ID,'Yjit1!43/#2^3' || 'Key!/#^') AS DB_ID     , PKG_CRYPTO.DECRYPT(A.DB_PWD,'Yjit1!43/#2^3' || 'Key!/#^') AS DB_PWD     , PKG_CRYPTO.DECRYPT(A.SVR_IP,'Yjit1!43/#2^3' || 'Key!/#^') AS SVR_IP     , B.AUTH_KEY     , B.MNGT_NO FROM CRM_CUST_SERVER A     LEFT OUTER JOIN  RPA_AUTH_KEY@DL_RPA B ON A.CUST_CD = B.CUST_CD WHERE A.CUST_CD = (SELECT CUST_CD FROM CRM_CUST_MST WHERE ELVIS_OFFICE_CD = '@OFFICE_CD')     AND A.SVR_TYPE ='D'     AND B.USE_YN ='Y' 
