DIV_B]        [IP_SEH_DIV_C]          AND A.SERVER_IP IN (SELECT NVL(Z.WEB_IP, Z.DB_IP) FROM SYS_CUST_MST@ELVIS Z WHERE (Z.WEB_IP_EXT = '@IP_SEH' OR Z.DB_IP_EXT = '@IP_SEH'))        [/IP_SEH_DIV_C]        [IP_SEH_DIV_D]          AND A.SERVER_DIV = '@IP_SEH'        [/IP_SEH_DIV_D]        [IP_SEH_DIV_E]          AND A.OS_INFO LIKE '%@IP_SEH%'        [/IP_SEH_DIV_E]        [IP_SEH_DIV_F]          AND A.CPU_INFO LIKE '@IP_SEH%'        [/IP_SEH_DIV_F]      [/IP_SEH]      GROUP BY A.SERVER_IP), W_SVR_PREVIOUSAS(   SELECT A.SERVER_IP        , MAX(A.SERVER_DIV) AS SERVER_DIV        , MAX(A.OS_INFO) AS OS_INFO        , MAX(A.CPU_INFO) AS CPU_INFO        , AVG(A.CPU_USE_RATE) AS CPU_USE_RATE        , AVG(A.RAM_USE_RATE) AS RAM_USE_RATE        , AVG(A.HDD_1_USE_RATE) AS HDD_1_USE_RATE        , AVG(A.HDD_2_USE_RATE) AS HDD_2_USE_RATE        , AVG(A.HDD_3_USE_RATE) AS HDD_3_USE_RATE        , AVG(A.HDD_4_USE_RATE) AS HDD_4_USE_RATE        , AVG(A.HDD_5_USE_RATE) AS HDD_5_USE_RATE        , AVG(A.HDD_6_USE_RATE) AS HDD_6_USE_RATE        , AVG(A.DB_PROC_USE) AS DB_PROC_USE        , AVG(A.DB_SESS_USE) AS DB_SESS_USE     FROM CRM_SERVER_INFO_LOG A    WHERE 1=1       AND A.CHECK_YMD >= TO_CHAR(ADD_MONTHS(TO_CHAR(TO_DATE('@DATE_FROM' || '01','YYYYMMDD'),'YYYY-MM-DD'),-3),'YYYYMMDD')      AND A.CHECK_YMD <= TO_CHAR(ADD_MONTHS(ADD_MONTHS(TO_CHAR(TO_DATE('@DATE_TO' || '01','YYYYMMDD'),'YYYY-MM-DD'),1)-1,-1),'YYYYMMDD')      AND A.DATA_DIV = 'SC'      [IP_SEH]        [IP_SEH_DIV_A]          AND (A.SERVER_IP = '@IP_SEH'          OR A.SERVER_IP IN (SELECT NVL(Z.WEB_IP, Z.DB_IP) FROM SYS_CUST_MST@ELVIS Z WHERE (Z.WEB_IP_EXT = '@IP_SEH' OR Z.DB_IP_EXT = '@IP_SEH'))          OR A.SERVER_DIV = '@IP_SEH'          OR A.OS_INFO LIKE '%@IP_SEH%'          OR A.CPU_INFO LIKE '%@IP_SEH%'          )        [/IP_SEH_DIV_A]        [IP_SEH_DIV_B]          AND A.SERVER_IP = '@IP_SEH'        [/IP_SEH_DIV_B]        [IP_SEH_DIV_C]           AND A.SERVER_IP IN (SELECT NVL(Z.WEB_IP, Z.DB_IP) FROM SYS_CUST_MST@ELVIS Z WHERE (Z.WEB_IP_EXT = '@IP_SEH' OR Z.DB_IP_EXT = '@IP_SEH'))         [/IP_SEH_DIV_C]        [IP_SEH_DIV_D]          AND A.SERVER_DIV = '@IP_SEH'        [/IP_SEH_DIV_D]        [IP_SEH_DIV_E]          AND A.OS_INFO LIKE '%@IP_SEH%'        [/IP_SEH_DIV_E]        [IP_SEH_DIV_F]          AND A.CPU_INFO LIKE '@IP_SEH%'        [/IP_SEH_DIV_F]      [/IP_SEH]      GROUP BY A.SERVER_IP)SELECT A.SERVER_IP     , (SELECT NVL(Z.WEB_IP_EXT, Z.DB_IP_EXT) FROM SYS_CUST_MST@ELVIS Z WHERE (Z.WEB_IP = A.SERVER_IP OR Z.DB_IP = A.SERVER_IP) AND ROWNUM <= 1) AS SERVER_IP_EXT     , MAX(B.SERVER_DIV) AS SERVER_DIV     , 'CPU' AS CPU_NM     , '메모리' AS RAM_NM     , 'HDD1' AS HDD_1_NM     , 'HDD2' AS HDD_2_NM     , 'HDD3' AS HDD_3_NM     , 'HDD4' AS HDD_4_NM     , 'HDD5' AS HDD_5_NM     , 'HDD6' AS HDD_6_NM     , '프로세스' AS PRC_NM     , '세션' AS SESS_NM     , 'HDD1 (3개월 뒤 예상)' AS HDD_1_3_NM     , 'HDD2 (3개월 뒤 예상)' AS HDD_2_3_NM     , 'HDD3 (3개월 뒤 예상)' AS HDD_3_3_NM     , 'HDD4 (3개월 뒤 예상)' AS HDD_4_3_NM     , 'HDD5 (3개월 뒤 예상)' AS HDD_5_3_NM     , 'HDD6 (3개월 뒤 예상)' AS HDD_6_3_NM     , ROUND(AVG(A.CPU_USE_RATE),1) AS CPU_USE_RATE     , ROUND(AVG(A.RAM_USE_RATE),1)AS RAM_USE_RATE     , ROUND(AVG(A.HDD_1_USE_RATE),1) AS HDD_1_USE_RATE     , ROUND(AVG(A.HDD_2_USE_RATE),1) AS HDD_2_USE_RATE     , ROUND(AVG(A.HDD_3_USE_RATE),1) AS HDD_3_USE_RATE     , ROUND(AVG(A.HDD_4_USE_RATE),1) AS HDD_4_USE_RATE     , ROUND(AVG(A.HDD_5_USE_RATE),1) AS HDD_5_USE_RATE     , ROUND(AVG(A.HDD_6_USE_RATE),1) AS HDD_6_USE_RATE     , ROUND((AVG(A.DB_PROC_USE) / AVG(A.DB_PROC_LIMIT) * 100),1) AS DB_PROC_USE     , ROUND((AVG(A.DB_SESS_USE) / AVG(A.DB_SESS_LIMIT) * 100),1) AS DB_SESS_USE     , ROUND(AVG(A.CPU_USE_RATE),1) AS CPU_USE_RATE_G     , ROUND(AVG(A.RAM_USE_RATE),1) AS RAM_USE_RATE_G     , ROUND(AVG(A.HDD_1_USE_RATE),1) AS HDD_1_USE_RATE_G     , ROUND(AVG(A.HDD_2_USE_RATE),1) AS HDD_2_USE_RATE_G     , ROUND(AVG(A.HDD_3_USE_RATE),1) AS HDD_3_USE_RATE_G     , ROUND(AVG(A.HDD_4_USE_RATE),1) AS HDD_4_USE_RATE_G     , ROUND(AVG(A.HDD_5_USE_RATE),1) AS HDD_5_USE_RATE_G     , ROUND(AVG(A.HDD_6_USE_RATE),1) AS HDD_6_USE_RATE_G     , ROUND((AVG(A.DB_PROC_USE) / AVG(A.DB_PROC_LIMIT) * 100),1) AS DB_PROC_USE_G     , ROUND((AVG(A.DB_SESS_USE) / AVG(A.DB_SESS_LIMIT) * 100),1) AS DB_SESS_USE_G     , MAX(SUBSTR(A.HDD_INFO, 1, 1)) AS HDD_1_INFO     , MAX(SUBSTR(A.HDD_INFO, 4, 1)) AS HDD_2_INFO     , MAX(SUBSTR(A.HDD_INFO, 7, 1)) AS HDD_3_INFO     , MAX(SUBSTR(A.HDD_INFO, 10, 1)) AS HDD_4_INFO     , MAX(SUBSTR(A.HDD_INFO, 13, 1)) AS HDD_5_INFO     , MAX(SUBSTR(A.HDD_INFO, 16, 1)) AS HDD_6_INFO     , MAX(SUBSTR(A.HDD_INFO, 1, 1)) AS HDD_1_INFO_B     , MAX(SUBSTR(A.HDD_INFO, 4, 1)) AS HDD_2_INFO_B     , MAX(SUBSTR(A.HDD_INFO, 7, 1)) AS HDD_3_INFO_B     , MAX(SUBSTR(A.HDD_INFO, 10, 1)) AS HDD_4_INFO_B     , MAX(SUBSTR(A.HDD_INFO, 13, 1)) AS HDD_5_INFO_B     , MAX(SUBSTR(A.HDD_INFO, 16, 1)) AS HDD_6_INFO_B     , ROUND(AVG(A.HDD_1_USE_RATE),1) AS HDD_1_USE_RATE_B     , ROUND(AVG(A.HDD_2_USE_RATE),1) AS HDD_2_USE_RATE_B     , ROUND(AVG(A.HDD_3_USE_RATE),1) AS HDD_3_USE_RATE_B     , ROUND(AVG(A.HDD_4_USE_RATE),1) AS HDD_4_USE_RATE_B     , ROUND(AVG(A.HDD_5_USE_RATE),1) AS HDD_5_USE_RATE_B     , ROUND(AVG(A.HDD_6_USE_RATE),1) AS HDD_6_USE_RATE_B     , ROUND(AVG(A.HDD_1_USE_RATE - B.HDD_1_USE_RATE),1) AS HDD_1_USE_RATE_A     , ROUND(AVG(A.HDD_2_USE_RATE - B.HDD_2_USE_RATE),1) AS HDD_2_USE_RATE_A     , ROUND(AVG(A.HDD_3_USE_RATE - B.HDD_3_USE_RATE),1) AS HDD_3_USE_RATE_A     , ROUND(AVG(A.HDD_1_USE_RATE - B.HDD_4_USE_RATE),1) AS HDD_4_USE_RATE_A     , ROUND(AVG(A.HDD_2_USE_RATE - B.HDD_5_USE_RATE),1) AS HDD_5_USE_RATE_A     , ROUND(AVG(A.HDD_3_USE_RATE - B.HDD_6_USE_RATE),1) AS HDD_6_USE_RATE_A     , ROUND(AVG(A.HDD_1_USE_RATE+(A.HDD_1_USE_RATE - B.HDD_1_USE_RATE)),1) AS HDD_1_USE_RATE_A_G     , ROUND(AVG(A.HDD_2_USE_RATE+(A.HDD_2_USE_RATE - B.HDD_2_USE_RATE)),1) AS HDD_2_USE_RATE_A_G     , ROUND(AVG(A.HDD_3_USE_RATE+(A.HDD_3_USE_RATE - B.HDD_3_USE_RATE)),1) AS HDD_3_USE_RATE_A_G     , ROUND(AVG(A.HDD_1_USE_RATE+(A.HDD_1_USE_RATE - B.HDD_4_USE_RATE)),1) AS HDD_4_USE_RATE_A_G     , ROUND(AVG(A.HDD_2_USE_RATE+(A.HDD_2_USE_RATE - B.HDD_5_USE_RATE)),1) AS HDD_5_USE_RATE_A_G     , ROUND(AVG(A.HDD_3_USE_RATE+(A.HDD_3_USE_RATE - B.HDD_6_USE_RATE)),1) AS HDD_6_USE_RATE_A_G  FROM W_SVR_CURRENT A    LEFT OUTER JOIN W_SVR_PREVIOUS B  ON A.SERVER_IP = B.SERVER_IP  GROUP BY A.SERVER_IP                   
