#SysHelpMgt@GetChildNodes
-- SysHelpMgt@GetChildNodesSELECT NODE_ID 	 , NODE_TYPE 	 , PARENT_NODE_ID 	 , LANG_CD  FROM CRM_FAQ_MST WHERE (PARENT_NODE_ID='@NODE_ID'         OR NODE_ID='@NODE_ID')    AND NODE_TYPE = 'N'ORDER BY NODE_ID
