#SysHelpMgt@GetNewNodeID
--SysHelpMgt@GetNewNodeIDSELECT '@NODE_TYPE'||TO_CHAR(SYSDATE,'YYMMDD')||LPAD(cast(NVL(MAX(SUBSTR(NODE_ID,8,4)),0) AS INT) + 1 ,4,'0000') AS NEW_ID  FROM CRM_FAQ_MSTWHERE SUBSTR(NODE_ID,2,6) = TO_CHAR(SYSDATE,'YYMMDD')
#SysHelpMgt@GetNewNodeIDSys
--SysHelpMgt@GetNewNodeIDSysSELECT '@NODE_TYPE'||TO_CHAR(SYSDATE,'YYMMDD')||LPAD(cast(NVL(MAX(SUBSTR(NODE_ID,8,4)),0) AS INT) + 1 ,4,'0000') AS NEW_ID  FROM SYS_FAQ_MSTWHERE SUBSTR(NODE_ID,2,6) = TO_CHAR(SYSDATE,'YYMMDD')
