#SysHelpMgt@GetNodeList
-- SysHelpMgt@GetNodeListSELECT NODE_ID 	 , NODE_TYPE 	 , PARENT_NODE_ID 	 , TITLE 	 , DISPLAY_SEQ	 , LANG_CD	 , INS_USR 	 , INS_DATE 	 , UPD_USR 	 , UPD_DATE FROM CRM_FAQ_MSTWHERE 1=1  [SEARCH_KEY]     AND ( 1=2           OR TITLE LIKE '%@SEARCH_KEY%'           OR CONTENT LIKE '%@SEARCH_KEY%'          )    [/SEARCH_KEY]ORDER BY DISPLAY_SEQ, NODE_ID
