#SysHelpMgt@InsertNode
--SysHelpMgt@InsertNodeINSERT INTO CRM_FAQ_MST(	 NODE_ID 	,NODE_TYPE 	,PARENT_NODE_ID 	,TITLE 	,DISPLAY_SEQ	,INS_USR 	,INS_DATE)VALUES (	 '@NODE_ID' 	,'@NODE_TYPE' 	,'@PARENT_NODE_ID' 	,'@TITLE' 	,(SELECT NVL(MAX(DISPLAY_SEQ),0)+1 	    FROM CRM_FAQ_MST 	   WHERE PARENT_NODE_ID='@PARENT_NODE_ID')	,'@INS_USR' 	,SYSDATE)
