-- SysHelpMgt@UpdateContentDECLARE    CONT VARCHAR2(32767);BEGIN	CONT :='@CONTENT';	UPDATE CRM_FAQ_MST	   SET NODE_TYPE = '@NODE_TYPE'	     , PARENT_NODE_ID = '@PARENT_NODE_ID'	     , TITLE = '@TITLE'	     , CONTENT=CONT	     , LANG_CD = '@LANG_CD'	     , UPD_USR='@UPD_USR' 	     , UPD_DATE= SYSDATE	 WHERE NODE_ID = '@NODE_ID';END;
