#TmsTrfMgt@DeleteWG1
-- TmsTrfMgt@DeleteWG1DELETE TMS_TRF_STD_CRM WHERE TRF_TYPE 	= '@TRF_TYPE'   [FR_PORT]       AND FR_PORT 		= '@FR_PORT'   [/FR_PORT]   [FR_PORT_NULL]       AND FR_PORT		IS NULL   [/FR_PORT_NULL]   [ONEWAY_DEPOT]       AND ONEWAY_DEPOT = '@ONEWAY_DEPOT'   [/ONEWAY_DEPOT]   [ONEWAY_DEPOT_NULL]       AND ONEWAY_DEPOT IS NULL   [/ONEWAY_DEPOT_NULL]   [CITY]       AND CITY 		= '@CITY'   [/CITY]   [CITY_NULL]       AND CITY 		IS NULL   [/CITY_NULL]   [STREET]       AND STREET 		= '@STREET'   [/STREET]   [STREET_NULL]       AND STREET 		IS NULL   [/STREET_NULL]         	
#TmsTrfMgt@InsertWG1
-- TmsTrfMgt@InsertWG1INSERT INTO TMS_TRF_STD_CRM (	   TRF_TYPE 	 , FR_PORT 	 , ONEWAY_DEPOT 	 , CITY 	 , STREET 	 , DISTANCE 	 , AMT_40F 	 , AMT_40F_CSM 	 , AMT_20F 	 , AMT_20F_CSM 	 , INS_USRID 	 , INS_YMD 	 , INS_HM) VALUES (	   '@TRF_TYPE' 	 , '@FR_PORT' 	 , '@ONEWAY_DEPOT' 	 , '@CITY' 	 , '@STREET' 	 , @DISTANCE 	 , @AMT_40F 	 , @AMT_40F_CSM 	 , @AMT_20F 	 , @AMT_20F_CSM 	 , '@INS_USRID' 	 , TO_CHAR(SYSDATE, 'yyyyMMdd')	 , TO_CHAR(SYSDATE, 'hh24miss'))
#TmsTrfMgt@SearchWG1
-- TmsTrfMgt@SearchWG1SELECT TRF_TYPE	 , FR_PORT	 , ONEWAY_DEPOT	 , CITY	 , STREET	 , DISTANCE	 , AMT_40F	 , AMT_40F_CSM	 , AMT_20F	 , AMT_20F_CSM  FROM TMS_TRF_STD_CRM WHERE 1=1   [TRF_TYPE]       AND TRF_TYPE	= '@TRF_TYPE'   [/TRF_TYPE]   [FR_PORT]       AND FR_PORT	= '@FR_PORT'   [/FR_PORT]   [ONEWAY_DEPOT]       AND ONEWAY_DEPOT	= '@ONEWAY_DEPOT'   [/ONEWAY_DEPOT]   [CITY]       AND CITY		= '@CITY'   [/CITY]   [STREET]       AND STREET 	= '@STREET'   [/STREET]   [FR_DISTANCE]   	   AND DISTANCE	>= '@FR_DISTANCE'   [/FR_DISTANCE]   [TO_DISTANCE]   	   AND DISTANCE	<= '@TO_DISTANCE'   [/TO_DISTANCE] ORDER     BY TRF_TYPE     , FR_PORT     , ONEWAY_DEPOT     , CITY     , STREET
#TmsTrfMgt@UpdateWG1
-- TmsTrfMgt@UpdateWG1UPDATE TMS_TRF_STD_CRM   SET DISTANCE		= @DISTANCE 	 , AMT_40F		= @AMT_40F 	 , AMT_40F_CSM	= @AMT_40F_CSM 	 , AMT_20F		= @AMT_20F 	 , AMT_20F_CSM	= @AMT_20F_CSM 	 , UPD_USRID	= '@UPD_USRID' 	 , UPD_YMD		= TO_CHAR(SYSDATE, 'yyyyMMdd')	 , UPD_HM		= TO_CHAR(SYSDATE, 'hh24miss') WHERE TRF_TYPE 	= '@TRF_TYPE'   [FR_PORT]       AND FR_PORT 		= '@FR_PORT'   [/FR_PORT]   [FR_PORT_NULL]       AND FR_PORT		IS NULL   [/FR_PORT_NULL]   [ONEWAY_DEPOT]       AND ONEWAY_DEPOT = '@ONEWAY_DEPOT'   [/ONEWAY_DEPOT]   [ONEWAY_DEPOT_NULL]       AND ONEWAY_DEPOT IS NULL   [/ONEWAY_DEPOT_NULL]   [CITY]       AND CITY 		= '@CITY'   [/CITY]   [CITY_NULL]       AND CITY 		IS NULL   [/CITY_NULL]   [STREET]       AND STREET 		= '@STREET'   [/STREET]   [STREET_NULL]       AND STREET 		IS NULL   [/STREET_NULL]	
