#YjediSpecMgt@Delete
--YjediSpecMgt@DeleteDELETE YJEDI_SPEC_MST WHERE CUSTOMS_CD = '@CUSTOMS_CD'   AND SYS_ID = '@SYS_ID'   AND SEND_ID = '@SEND_ID'
#YjediSpecMgt@Save
--YjediSpecMgt@Save MERGE INTO YJEDI_SPEC_MST A USING    (SELECT '@CUSTOMS_CD' AS CUSTOMS_CD          , '@SYS_ID' AS SYS_ID          , '@SEND_ID' AS SEND_ID          , '@RECV_ID' AS RECV_ID          , '@FWD_QUAL' AS FWD_QUAL          , '@SEND_TYPE' AS SEND_TYPE          , '@SEND_PATH' AS SEND_PATH          , '@SEND_USR_ID' AS SEND_USR_ID          , '@SEND_PSWD' AS SEND_PSWD          , '@RECV_TYPE' AS RECV_TYPE          , '@RECV_PATH' AS RECV_PATH          , '@RECV_USR_ID' AS RECV_USR_ID          , '@RECV_PSWD' AS RECV_PSWD          , '@RMK' AS RMK          , '@USE_YN' AS USE_YN       FROM DUAL) B         ON (A.CUSTOMS_CD = B.CUSTOMS_CD AND A.SYS_ID = B.SYS_ID AND A.SEND_ID = B.SEND_ID)       WHEN NOT MATCHED THEN     INSERT (INS_USR           , INS_YMD           , INS_HM           , UPD_USR           , UPD_YMD           , UPD_HM           , CUSTOMS_CD           , SYS_ID           , SEND_ID           , RECV_ID           , FWD_QUAL           , SEND_TYPE           , SEND_PATH           , SEND_USR_ID           , SEND_PSWD           , RECV_TYPE           , RECV_PATH           , RECV_USR_ID           , RECV_PSWD           , RMK           , USE_YN)     VALUES ('@USR_ID'           , UFN_DATE_FORMAT('DATE')           , UFN_DATE_FORMAT('TIME')           , '@USR_ID'           , UFN_DATE_FORMAT('DATE')           , UFN_dATE_FORMAT('TIEM')           , B.CUSTOMS_CD           , B.SYS_ID           , B.SEND_ID           , B.RECV_ID           , B.FWD_QUAL           , B.SEND_TYPE           , B.SEND_PATH           , B.SEND_USR_ID           , B.SEND_PSWD           , B.RECV_TYPE           , B.RECV_PATH           , B.RECV_USR_ID           , B.RECV_PSWD           , B.RMK           , B.USE_YN)       WHEN MATCHED THEN     UPDATE        SET  UPD_USR = '@USR_ID'           , UPD_YMD = UFN_DATE_FORMAT('DATE')           , UPD_HM = UFN_DATE_FORMAT('TIME')           , RECV_ID = B.RECV_ID           , FWD_QUAL = B.FWD_QUAL           , SEND_TYPE = B.SEND_TYPE           , SEND_PATH = B.SEND_PATH           , SEND_USR_ID = B.SEND_USR_ID           , SEND_PSWD = B.SEND_PSWD           , RECV_TYPE = B.RECV_TYPE           , RECV_PATH = B.RECV_PATH           , RECV_USR_ID = B.RECV_USR_ID           , RECV_PSWD = B.RECV_PSWD           , RMK = B.RMK           , USE_YN = B.USE_YN
#YjediSpecMgt@Search
--YjediSpecMgt@SearchSELECT A.CUSTOMS_CD     , A.SYS_ID     , A.SEND_ID     , A.RECV_ID     , A.FWD_QUAL     , A.SEND_TYPE     , A.SEND_PATH     , A.SEND_USR_ID     , A.SEND_PSWD     , A.RECV_TYPE     , A.RECV_PATH     , A.RECV_USR_ID     , A.RECV_PSWD     , A.RMK     , A.USE_YN     , UFN_TABLE_ITEM('USER_NM',A.INS_USR,'L','') AS INS_USR     , TO_CHAR(TO_DATE(A.INS_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.INS_HM,'hh24miss'), 'hh24:mi:ss') AS INS_YMD     , UFN_TABLE_ITEM('USER_NM',A.UPD_USR,'L','') AS UPD_USR     , TO_CHAR(TO_DATE(A.UPD_YMD,'YYYYMMDD'),'YYYY-MM-DD') || ' ' ||       TO_CHAR(TO_DATE(A.UPD_HM,'hh24miss'), 'hh24:mi:ss') AS UPD_YMD  FROM YJEDI_SPEC_MST A WHERE 1 = 1 [CUSTOMS_CD]   AND CUSTOMS_CD = '@CUSTOMS_CD' [/CUSTOMS_CD] [SYS_ID]   AND SYS_ID = '@SYS_ID' [/SYS_ID] [SEND_ID]   AND SEND_ID = '@SEND_ID' [/SEND_ID] ORDER BY SEND_ID DESC
#YjediSpecMgt@SearchChk
--YjediSpecMgt@SearchChkSELECT A.CUSTOMS_CD AS CUSTOMS_CD  FROM YJEDI_SPEC_MST A WHERE A.CUSTOMS_CD = '@CUSTOMS_CD'
